import { Component } from "react";

export class Form extends Component {
  state = {
    name: "",
  };

  handleNameChange = (event) => {
    this.setState({
      name: event.currentTarget.value,
    });
  };
  render() {
    const { name } = this.state;
    return (
      <form>
        <label>
          <input
            type="text"
            placeholder="Введите имя"
            value={name}
            onChange={this.handleNameChange}
          />
        </label>
      </form>
    );
  }
}
