import { Component } from "react";

export class Form extends Component {
  state = {
    name: "",
    tag: "",
    experience: "junior",
  };

  handleInputChange = (event) => {
    const { name, value } = event.currentTarget;
    this.setState({
      [name]: value,
    });
  };

  handleSubmit = (event) => {
    event.preventDefault();
    this.props.onSubmit(this.state);
    this.reset();
  };

  reset = () => {
    this.setState({ name: "", tag: "" });
  };

  render() {
    const { name, tag } = this.state;
    return (
      <form
        style={{
          display: "flex",
          flexDirection: "column",
        }}
        onSubmit={this.handleSubmit}
      >
        <label>
          <input
            type="text"
            name="name"
            placeholder="Введите имя"
            value={name}
            onChange={this.handleInputChange}
          />
        </label>
        <br />
        <label>
          <input
            type="text"
            name="tag"
            placeholder="Введите никнейм"
            value={tag}
            onChange={this.handleInputChange}
          />
        </label>
        <p>Ваш уровень разработчика</p>
        <label>
          Junior
          <input type="radio" name="experince" value="junior" />
        </label>
        <label>
          Middle
          <input type="radio" name="experince" value="middle" />
        </label>
        <label>
          Senior
          <input type="radio" name="experince" value="senior" />
        </label>
        <button style={{ width: "100px", height: "25px", marginTop: "10px" }} type="submit">
          Отправить
        </button>
      </form>
    );
  }
}
